
using Breakfast.Models;
using CommunityToolkit.Mvvm.ComponentModel;
using CommunityToolkit.Mvvm.Input;

namespace Breakfast.ViewModels
{
    public partial class BreakfastVm : ObservableObject
    {
        [ObservableProperty]
        List<BreakfastM> breakfasts;

        [ObservableProperty]
        bool isRefreshing;
        public BreakfastVm()
        {
           
        }
        [RelayCommand]
        public async Task LoadBreakfastsAsync()
        {
            try
            {
                IsRefreshing= true; 
            await Task.Delay(2000);
            Breakfasts = new()
            {
                new BreakfastM(
                  Name:  "Tica massala",
                  Description:  "Chicken tikka masala is a South Asian dish consisting of roasted marinated chicken chunks in a spiced sauce",
                 StartDateTime:   new DateTime(),
                 EndDateTime:   new DateTime(),
                  Image:  new Uri("data:image/jpeg;base64,"),
                Savory:  new List<string>(){"hello","albert"},
                   Sweet: new List<string>(){"sweet","more sweet","tz","tanganyika","bongo","tanzania"}
                ),
                new BreakfastM(
                  Name:  "Aos simtengu",
                  Description:  "Chicken tikka masala is a South Asian dish consisting of roasted marinated chicken chunks in a spiced sauce",
                 StartDateTime:   new DateTime(),
                 EndDateTime:   new DateTime(),
                 Image: new Uri("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTqzrUZmKGJxGkISnQXEHdQwHiNNjlfCQ5TfijBZypHccN6s_KyHwhC&usqp=CAE&s"),
                Savory:  new List<string>(){"aos","albert","richards","rabias"},
                   Sweet: new List<string>(){"sweet","more sweet"}
                )
            };

            }
            finally
            {
              IsRefreshing= false;  
            }
        }

        [RelayCommand]
        public async Task GoToCreateBreakfastPage()
        {
            await Shell.Current.GoToAsync(nameof(CreateBreakfastPage));
        }
    }
}
